    },
    payload: {
        "email": uid,
        "password": pwd,
        "client_specifier": "iPhone 9.2 | 2.2 | 222"
    }
};
return newMsg;

if (typeof context.global.Blink == 'undefined'){
    context.global.Blink = {};
    context.global.Blink.uid = ""; // Enter your Blink User e-mail here
    context.global.Blink.pwd = ""; // Enter your Blink password here
}
return msg;

var newMsg2 ={
    "url":"https://prod.immedia-semi.com/network/"+context.global.Blink.network.id+"/syncmodules",
    "method": "GET",
    headers: {
        "Content-Type":"application/json",
        "TOKEN_AUTH":context.global.Blink.token
    }
};

var newMsg3 ={
    "url":"https://prod.immedia-semi.com/homescreen",
    "method": "GET",
    headers: {
        "Content-Type":"application/json",
        "TOKEN_AUTH":context.global.Blink.token
    }
};

var newMsg4 ={
    "url":"https://prod.immedia-semi.com/events/network/"+context.global.Blink.network.id,
    "method": "GET",
    headers: {
        "Content-Type":"application/json",
        "TOKEN_AUTH":context.global.Blink.token
    }
};

return [newMsg2, newMsg3, newMsg4];

mp = msg.payload.networks[0];

context.global.Blink.network={
	'id' : mp.id,
	'armed' : mp.armed,
	'autoarm_geo_enable' : mp.autoarm_geo_enable,
	'autoarm_time_enable' : mp.autoarm_time_enable,
	'video_destination' : mp.video_destination,
	'storage_used' : mp.storage_used,
	'storage_total' : mp.storage_total,
	'video_count' : mp.video_count,
	'busy' : mp.busy,
	'camera_error' : mp.camera_error,
	'sync_module_error' : mp.sync_module_error
};
context.global.Blink.account_id = mp.account_id;
return msg;

var sm = {};
sm = msg.payload.syncmodule;
context.global.Blink.syncmodule={
    'id':sm.id,
    'updated':sm.updated_at,
    'name':sm.name,
    'mac_address':sm.mac_address,
    'serial':sm.serial,
    'status':sm.status,
    'onboarded':sm.onboarded,
    'server':sm.server
};
return msg;

context.global.executeBlinkCMD = function(command){
    var BlinkMsg={};
    switch (command.toLowerCase()){
        case 'arm':
        case 'disarm':
        BlinkMsg = {
            "url":"https://prod.immedia-semi.com/network/"+context.global.Blink.network.id+"/"+command,
            method: "POST",
            headers: {
                "TOKEN_AUTH":context.global.Blink.token
            },
            payload:{}
        }
        break;
    }
    if (typeof BlinkMsg.url!=='undefined' && BlinkMsg.url!=="")
    {
        node.send(BlinkMsg);
    }
};
return;

context.global.Blink.wifi_strength=msg.payload.network.wifi_strength;
context.global.Blink.notification_count = msg.payload.network.notifications;
context.global.Blink.cameras = {};
var cameras = [];
cameras = msg.payload.devices;
for (i=0; i<cameras.length; i++) {
    if (typeof cameras[i].name!=='undefined'){
        context.global.Blink.cameras[cameras[i].name]={
            'device_type':cameras[i].device_type,
            'device_id':cameras[i].device_id,
            'updated_at':cameras[i].updated_at,
            'name':cameras[i].name,
            'thumbnail':cameras[i].thumbnail,
            'active':cameras[i].active,
            'notifications':cameras[i].notifications,
            'enabled':cameras[i].enabled,
            'armed':cameras[i].armed,
            'wifi_strength':cameras[i].wifi_strength,
            'lfr_strength':cameras[i].lfr_strength,
            'temp':cameras[i].temp,
            'battery':cameras[i].battery
        };
    }
}
return msg;

for (var key in context.global.Blink.cameras)
{
    delete context.global.winkState.cameras[key];
}
delete context.global.Blink;

node.warn("Blink data has been cleared.")

context.global.Blink.events = {};
if (typeof context.global.Blink.lastevent=="undefined"){ context.global.Blink.lastevent=0 }
var events = [];
events = msg.payload.event;
var matches=0;
for (i=0; i<events.length; i++) {
    if (typeof events[i].id!=='undefined'){
        context.global.Blink.events[events[i].id]={
            'id':events[i].id,
            'updated_at':events[i].updated_at,
            'type':events[i].type,
            'notified':events[i].notified,
            'camera_id':events[i].camera_id,
            'sync_module_id':events[i].sync_module_id,
            'network_id':events[i].network_id,
            'account_id':events[i].account_id,
            'syncmodule':events[i].syncmodule,
            'camera':events[i].camera,
            'account':events[i].account,
            'camera_name':events[i].camera_name,
            'video_id':events[i].video_id,
            'video_url':events[i].video_url
        };
    }
}
var events = [];
events=context.global.Blink.events;
for (var key in events) {
    if (events[key].type=="motion" && events[key].id>context.global.Blink.lastevent)
    {
        context.global.winkState.cameras[events[key].camera_name].snap_url=context.global.BlueMixUrlBase+"/blink/snap?camera_name="+events[key].camera_name+"&blinkevent="+events[key].id+"&iftttkey="+context.global.FREEBOARD_TOKEN;
        newmsg={
            "url":context.global.BlueMixUrlBase+'/red/save_images?cam_list='+context.global.camera_motion[events[key].camera_name]+'&object_name='+events[key].camera_name,
            "method": "GET",
            headers: {
                "Authorization":"Bearer "+context.global.FREEBOARD_TOKEN
            }
        };
        node.warn("New event imported: "+events[key].id+" for camera "+events[key].camera_name);
        node.send(newmsg);
        context.global.Blink.lastevent=events[key].id;
        matches+=1;
        break;
    }
}
if (matches===0){

    for (var key in context.global.winkState.cameras)
    {
        if (context.global.winkState.cameras[key].eco_system=="Blink"){
            context.global.winkState.cameras[key].snap_url=context.global.BlueMixUrlBase+"/blink/snap?camera_name="+context.global.winkState.cameras[key].name;
        }
    }
}
return;

var web_req=(context.global.getCookie('exchange_token',msg.req.headers.cookie)=='0' ? false:true);
if (msg.payload.iftttkey==context.global.IFTTT_TOKEN || msg.payload.iftttkey==context.global.FREEBOARD_TOKEN || (msg.req.headers.authorization && msg.req.headers.authorization=="Bearer "+context.global.FREEBOARD_TOKEN)) web_req=true;
if (web_req)
{
    var command=msg.req.originalUrl.split('/').pop();
    msg.statusCode=200;
    context.global.executeBlinkCMD(command);
}
else {
    node.warn("ifttt messages bad request");
    msg.statusCode=403;
}
return msg;

for (var key in context.global.Blink.cameras)
{
    var bc = context.global.Blink.cameras[key];
    context.global.camera_motion[bc.name]=[bc.name]
    context.global.winkState.cameras[bc.name]={
        'name':bc.name,
        'object_type':"cameras",
        'eco_system':"Blink",
        'object_id':bc.device_id,
        'manufacturer_device_model':"Blink Camera",
        'connection':true,
        'capturing_audio':false,
        'capturing_video':false,
        'url':context.global.BlueMixUrlBase+"/blink/thumb?camera_name="+bc.name,
        'snap_url':context.global.BlueMixUrlBase+"/blink/snap?camera_name="+bc.name,
        'history_url':context.global.BlueMixUrlBase+"/freeboard/cameraSnapshots?token="+context.global.FREEBOARD_TOKEN+"&camera_name="+bc.name,
        'refresh_time':30,
        'motion': "N/A",
        'activities':[]
    };
    context.global.winkState.sensor_pods[bc.name]={
        'eco_system':"Blink",
        'name':bc.name,
        'object_type':"sensor_pods",
        'object_id':bc.device_id,
        'device_manufacturer':"Blink",
        'temperature':((bc.temp-32)*5/9),
        'connection':true
    };
    context.global.winkDevCap[bc.name]={
        "fields":[{"field":"temperature","mutability":"read-only","type":"float"}]
    };
    var newmsg ={
        "url":context.global.BlueMixUrlBase+'/red/retrieve_activities?camera_name='+bc.name,
        "method": "GET",
        headers: {
            "Authorization":"Bearer "+context.global.FREEBOARD_TOKEN
        }
    };
    node.send(newmsg);
}

return;

var web_req=(context.global.getCookie('exchange_token',msg.req.headers.cookie)=='0' ? false:true);
if (msg.payload.iftttkey==context.global.IFTTT_TOKEN || msg.payload.iftttkey==context.global.FREEBOARD_TOKEN || (msg.req.headers.authorization && msg.req.headers.authorization=="Bearer "+context.global.FREEBOARD_TOKEN)) web_req=true;
if (web_req)
{
    msg.statusCode=200;
    msg.url="https://prod.immedia-semi.com"+context.global.Blink.cameras[msg.payload.camera_name].thumbnail+".jpg";
    msg.method="GET";
    msg.headers={"TOKEN_AUTH":context.global.Blink.token};
    return [msg,null];
}
else {
    node.warn("ifttt messages bad request");
    msg.payload="403: Bad request";
    msg.statusCode=403;
    return [null,msg];
}
return msg;

var web_req=(context.global.getCookie('exchange_token',msg.req.headers.cookie)=='0' ? false:true);
if (msg.payload.iftttkey==context.global.IFTTT_TOKEN || msg.payload.iftttkey==context.global.FREEBOARD_TOKEN || (msg.req.headers.authorization && msg.req.headers.authorization=="Bearer "+context.global.FREEBOARD_TOKEN)) web_req=true;
context.global.snapreq = {};
if (web_req)
{
    if(typeof msg.payload.blinkevent!=="undefined")
    {
        msg.payload={
            'camera_name':msg.payload.camera_name,
            'blinkevent':msg.payload.blinkevent
        }
    }
    else {
        msg.payload={
            'camera_name':msg.payload.camera_name,
        }
    }
    return [msg,null];
}
else {
    node.warn("ifttt messages bad request");
    msg.statusCode=403;
    msg.payload="403: Bad request";
    return [null,msg];
}

var events = context.global.Blink.events;
var id = "0";
var snap_url="";
for (var e in events)
{
    if(events[e].type=="motion")
    {
        if((typeof msg.payload.blinkevent == "undefined") && events[e].camera_name == msg.payload.camera_name && events[e].id>id)
        {
            id=events[e].id;
            snap_url="https://prod.immedia-semi.com/"+events[e].video_url.replace(".mp4",".jpg");
        }
        else if (msg.payload.blinkevent>0 && events[e].id==msg.payload.blinkevent && events[e].camera_name == msg.payload.camera_name)
        {
            id=events[e].id;
            snap_url="https://prod.immedia-semi.com/"+events[e].video_url.replace(".mp4",".jpg");
        }
    }
}
if (id>1 && snap_url!=="" && snap_url!=="https://prod.immedia-semi.com/events/network/"+context.global.Blink.network.id)
{
    msg.url=snap_url;
    msg.method="GET";
    msg.headers={"TOKEN_AUTH":context.global.Blink.token};
}
else
{
    msg.url="https://prod.immedia-semi.com"+context.global.Blink.cameras[msg.payload.camera_name].thumbnail+".jpg";
    msg.method="GET";
    msg.headers={"TOKEN_AUTH":context.global.Blink.token};
}
return msg;
