    if (msg.payload.name.substring(0,1)!=="." && msg.payload.name.substring(0,1)!=="@") 
    {
        //node.warn(msg.payload.name);
        if (typeof context.global.winkChanged[msg.payload.name]==='undefined') { context.global.winkChanged[msg.payload.name] = new Object() }

        if (typeof context.global.winkChanged[msg.payload.name].new_state==='undefined') 
