            context.global.winkChanged[msg.payload.name].old_state=context.global.winkChanged[msg.payload.name].new_state;
        }

        if ('last_reading' in msg.payload && 'temperature' in msg.payload.last_reading)
        {
            if ('occupied' in msg.payload.last_reading)
            {
                context.global.winkChanged[msg.payload.name].new_state = {
                    occupied:msg.payload.last_reading.occupied,
                    temperature:((msg.payload.last_reading.temperature)*1.8+32).toFixed(2)
                }
            } else {
            context.global.winkChanged[msg.payload.name].new_state = ((msg.payload.last_reading.temperature)*1.8+32).toFixed(2);
            }
        }

        if (msg.payload.model_name=="Motion Sensor")
        {
            context.global.winkChanged[msg.payload.name].new_state = (msg.payload.last_reading.motion ? "motion detected":"no motion")
        }

        if (msg.payload.model_name=="Tripper") 
        {
            context.global.winkChanged[msg.payload.name].new_state = (msg.payload.last_reading.opened ? "Opened":"Closed")
        }

        if ('last_reading' in msg.payload && 'locked' in msg.payload.last_reading) 
        {
            context.global.winkChanged[msg.payload.name].new_state = (msg.payload.last_reading.locked ? "Locked":"Unlocked")
        }

        if (msg.payload.object_type=="binary_switch" && (!('powered' in msg.payload.desired_state) || msg.payload.desired_state.powered===false) )
        {
            context.global.winkChanged[msg.payload.name].new_state = (msg.payload.last_reading.powered ? "On":"Off")
        }

        if (msg.payload.object_type=="garage_doors")
        {
            context.global.winkChanged[msg.payload.name].new_state = (msg.payload.last_reading.garage_doors=="0" ? "Closed":"Opened")
        }

        if (msg.payload.object_type=="light_bulb" && !('powered' in msg.payload.desired_state))
        {
            context.global.winkChanged[msg.payload.name].new_state = {
                powered:(msg.payload.last_reading.powered ? "On":"Off"),
                brightness:(msg.payload.last_reading.powered ? msg.payload.last_reading.brightness*100 : 0)
            }
        }

        if ('liquid_detected' in msg.payload)
        {
            context.global.winkChanged[msg.payload.name].new_state = (msg.payload.last_reading.liquid_detected ? "Water detected":"No water detected")
        }

        if (msg.payload.object_type =="propane_tanks")
        {
            context.global.winkChanged[msg.payload.name].new_state = msg.payload.last_reading.remaining
        }

        if (msg.payload.object_type =="powerstrips")
        {
            context.global.winkChanged[msg.payload.name].new_state = msg.payload.outlets[0].name+" "+ (msg.payload.outlets[0].powered ? "On, ":"Off, ")+msg.payload.outlets[0].name+" "+(msg.payload.outlets[1].powered ? "On":"Off")
        }

        //node.warn(msg.payload.name+" changed.");
        if (context.global.winkChanged[msg.payload.name].old_state!==context.global.winkChanged[msg.payload.name].new_state)
        {
            if (typeof context.global.winkChanged[msg.payload.name].new_state.powered==='undefined')
            {
                node.warn(msg.payload.name+" changed from "+context.global.winkChanged[msg.payload.name].old_state+" to "+context.global.winkChanged[msg.payload.name].new_state);
            } 
            else 
            {
                if (context.global.winkChanged[msg.payload.name].old_state.powered!==context.global.winkChanged[msg.payload.name].new_state.powered)
                {
                    node.warn(msg.payload.name+" turned "+context.global.winkChanged[msg.payload.name].new_state.powered+" (Brightness: "+context.global.winkChanged[msg.payload.name].new_state.brightness+"%)");    
                }
                else if (context.global.winkChanged[msg.payload.name].new_state.powered=="On")
                {
                    node.warn(msg.payload.name+" changed brightness from "+context.global.winkChanged[msg.payload.name].old_state.brightness+"% to "+context.global.winkChanged[msg.payload.name].new_state.brightness+"%");
                }
            }
        }

        var newMsg = {
            url:context.global.BlueMixUrlBase+'/red/robots',
            "method": "POST",
            headers: {
                "Authorization":"Bearer "+context.global.FREEBOARD_TOKEN
            },
            payload:{
                'name':msg.payload.name,
                'object_type':msg.payload.object_type,
                'new_state':context.global.winkChanged[msg.payload.name].new_state,
                'old_state':context.global.winkChanged[msg.payload.name].old_state
            }
        };
        return newMsg;
    }
