                    "3": 1
                },
                "col": {
                    "3": 1
                },
                "col_width": 1,
                widgets:[]
            });
            pane_idx.motion=i;
            pane_id=i;
            i++;
        }
    } else if (typeof context.global.winkState.sensor_pods[key].opened !== 'undefined') 
