            i++;
        }

    } else
    {
        //other sensors pane
        if( typeof pane_idx.other !=='undefined') 
        {
            pane_id=pane_idx.other; //motion
        } else
        {
            freeboard.panes.push(  {
                "title":"Multi Sensors",
