                widgets:[]
            });
            pane_idx.other=i;
            pane_id=i;
            i++;
        }
    }
    if(context.global.winkDevCap[key].sensor_types)
    {
        for (s=0; s<context.global.winkDevCap[key].sensor_types.length;s++)
        {
            var field=context.global.winkDevCap[key].sensor_types[s].field
            if( context.global.winkState.sensor_pods[key][field] !== null)
            {
                freeboard.panes[pane_id].widgets.push(
                 {
                     "type": "indicator",
                     "settings": 
                      {
                          "title":key + " " + field.replace("_"," ").replace(/opened|detected|motion|state/,""),
                          "value":"wink.indicator.value(datasources[\"Node-Red\"] && datasources[\"Node-Red\"][\"WinkData\"][\"sensor_pods\"][\"" + key + "\"],\"" + field +"\")",
                          "on_text":"wink.indicator.on_text(datasources[\"Node-Red\"] && datasources[\"Node-Red\"][\"WinkData\"][\"sensor_pods\"][\"" + key + "\"],\"" + field +"\")",
                          "off_text":"wink.indicator.off_text(datasources[\"Node-Red\"] && datasources[\"Node-Red\"][\"WinkData\"][\"sensor_pods\"][\"" + key + "\"],\"" + field +"\")",
                          "style_element":"wink.indicator.style_element(datasources[\"Node-Red\"] && datasources[\"Node-Red\"][\"WinkData\"][\"sensor_pods\"][\"" + key + "\"],\"" + field +"\")"
                      }
